{
  "requestBody": {
    "size": 0,
    "query": {
        "bool": {
            "must": [
                {
                    "term": {
                        "monitor.type": "icmp"
                    }
                }
            ],
            "filter": [
                {
                    "range": {
                        "@timestamp": {
                            "format": "strict_date_optional_time",
                            "gte": "{0}",
                            "lte": "{1}"
                        }
                    }
                }
            ]
        }
    },
    "aggs": {
        "hourly_buckets": {
            "date_histogram": {
                "field": "@timestamp",
                "calendar_interval": "1d"
            },
            "aggs": {
                "host_count": {
                    "cardinality": {
                        "field": "Hex_Hostname"
                    }
                }
            }
        },
        "average": {
            "avg_bucket": {
                "buckets_path": "hourly_buckets>host_count"
            }
        }
    }
},
  "requestBody1": null,
  "requestBody2": null,
  "requestBody3": null,
  "apiUrl": "/heartbeat-*/_search",
  "apiUrl1": null,
