{
  "requestBody": {
    "size": 0,
    "query": {
      "bool": {
        "must": [
          {
            "terms": {
              "Hex_DeviceCategory": [
                "Windows Server",
                "active directory",
                "activedirectory",
                "application",
                "application server",
                "azure stack hci",
                "backup",
                "backup media agents"
              ]
            }
          }
        ],
        "filter": {
          "range": {
            "@timestamp": {
              "format": "strict_date_optional_time||epoch_millis",
              "gte": "{0}",
              "lt": "{1}"
            }
          }
        }
      }
    },
    "aggs": {
      "os_type": {
        "terms": {
          "field": "Hex_HostOS"
        },
        "aggs": {
          "numerator": {
            "filter": {
              "bool": {
                "filter": [
                  {
                    "bool": {
                      "should": [
                        {
                          "match_phrase": {
                            "monitor.status": "up"
                          }
                        }
                      ],
                      "minimum_should_match": 1
                    }
                  }
                ]
              }
            },
            "aggs": {
              "host_up": {
                "cardinality": {
                  "field": "Hex_Hostname"
                }
              }
            }
          },
          "denominator": {
            "filter": {
              "bool": {
                "filter": [
                  {
                    "bool": {
                      "should": [
                        {
                          "bool": {
                            "should": [
                              {
                                "match_phrase": {
                                  "monitor.status": "down"
                                }
                              }
                            ],
                            "minimum_should_match": 1
                          }
                        },
                        {
                          "bool": {
                            "should": [
                              {
                                "match_phrase": {
                                  "monitor.status": "up"
                                }
                              }
                            ],
                            "minimum_should_match": 1
                          }
                        }
                      ],
                      "minimum_should_match": 1
                    }
                  }
                ]
              }
            },
            "aggs": {
              "status": {
                "terms": {
                  "field": "monitor.status"
                },
                "aggs": {
                  "host": {
                    "cardinality": {
                      "field": "Hex_Hostname"
                    }
                  }
                }
              }
            }
          },
          "percentage_up": {
            "bucket_script": {
              "buckets_path": {
                "numerator": "numerator>_count",
                "denominator": "denominator>_count"
              },
              "script": "(params.numerator != null && params.denominator != null && params.denominator > 0 ? params.numerator / params.denominator : 0)*100"
            }
          }
        }
      }
    }
  },
  "requestBody1": null,
  "requestBody2": null,
  "requestBody3": null,
  "apiUrl": "/heartbeat-*/_search",
  "apiUrl1": null,
  "apiUrl2": null,
  "apiUrl3": null
}
